    --popover: 0 0% 100%;
    --popover-foreground: 270 25% 20%;

    /* Primary - Lilac */
    --primary: 270 60% 70%;
    --primary-foreground: 0 0% 100%;
    --primary-dark: 270 50% 60%;
    --primary-light: 270 70% 85%;

    /* Secondary - Soft purple */
    --secondary: 280 40% 95%;
    --secondary-foreground: 270 50% 30%;

    --muted: 270 20% 96%;
    --muted-foreground: 270 20% 45%;

    /* Accent - Lavender */
    --accent: 250 70% 90%;
    --accent-foreground: 270 50% 30%;

    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 0 0% 100%;

    --border: 270 20% 91%;
    --input: 270 20% 91%;
    --ring: 270 60% 70%;

    --radius: 0.75rem;

    /* Gradients */
    --gradient-primary: linear-gradient(135deg, hsl(270 60% 70%), hsl(280 65% 75%));
    --gradient-soft: linear-gradient(180deg, hsl(270 70% 95%), hsl(0 0% 100%));
    --gradient-accent: linear-gradient(135deg, hsl(250 70% 85%), hsl(270 60% 70%));
    --gradient-hero: linear-gradient(180deg, hsl(270 60% 70% / 0.1), hsl(0 0% 100% / 0.9));

    /* Shadows */
    --shadow-soft: 0 4px 20px -2px hsl(270 60% 70% / 0.15);
    --shadow-card: 0 10px 40px -10px hsl(270 60% 70% / 0.1);
    --shadow-hover: 0 20px 60px -15px hsl(270 60% 70% / 0.2);

    /* Transitions */
    --transition-smooth: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    --transition-bounce: all 0.5s cubic-bezier(0.68, -0.55, 0.265, 1.55);

    /* Purple ribbon symbol color */
    --ribbon-color: 270 60% 70%;
  }

}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
  }
}

